

public class assignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=20;
int b=5;
int c;
c=a+b;


System.out.println("The assignment operator = "+c);

c+=a;
System.out.println("The assignment operator += " +c);
b-=a;
System.out.println("The assignment operator -= " +b);

c*=a;
System.out.println("The assignment operator *= " +c);
	
c/=a;
System.out.println("The assignment operator /= " +c);

c%=a;
System.out.println("The assignment operator %= " +c);

	}
	
	
	
	
	}


